clear; clc; close all;addpath(genpath('./3Dtools'));mesh_dir='../../data/cover/PMN_trimmed/';mesh = dir(mesh_dir);mesh_num=size(mesh)-2;k=10^(-6);% allowed vertex chan rangeI=(0:15)-7;save_pth = './cost/cost1';parpool(32);for i=1:mesh_num    name = mesh(i+2).name;    b=strsplit(name,'.');    cost1(b{1},fullfile(mesh_dir,name),k,I,save_pth);endpoolobj = gcp('nocreate');delete(poolobj);